  backoffMs: number;
  maxBackoffMs: number;
}

const DEFAULT_TIMEOUT = 30000;
const MAX_RETRIES = 3;
const INITIAL_BACKOFF = 1000;
const MAX_BACKOFF = 30000;

export class HttpClient {
  private client: AxiosInstance;
  private logger: Logger;
  private retryConfig: RetryConfig;

  constructor(baseURL: string, timeout = DEFAULT_TIMEOUT) {
    this.client = axios.create({ baseURL, timeout });
    this.logger = new Logger('HttpClient');
    this.retryConfig = {
      maxRetries: MAX_RETRIES,
      backoffMs: INITIAL_BACKOFF,
      maxBackoffMs: MAX_BACKOFF
    };
  }

  private async sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  private calculateBackoff(attempt: number): number {
    const backoff = Math.min(
      this.retryConfig.backoffMs * Math.pow(2, attempt),
      this.retryConfig.maxBackoffMs
    );
    return backoff;
  }

  // Retry logic with exponential backoff
  private async executeWithRetry<T>(
    operation: () => Promise<T>,
    attempt = 0
  ): Promise<T> {
    try {
      return await operation();
    } catch (error) {
      if (attempt >= this.retryConfig.maxRetries) {
        this.logger.error('Max retries exceeded', { attempt, error });
        throw error;
      }

      const backoff = this.calculateBackoff(attempt);
      this.logger.warn('Request failed, retrying...', { attempt, backoff });
      await this.sleep(backoff);
      return this.executeWithRetry(operation, attempt + 1);
    }
  }

  async get<T>(url: string, config?: AxiosRequestConfig): Promise<T> {
    return this.executeWithRetry(() =>
      this.client.get<T>(url, config).then(res => res.data)
    );
  }

  async post<T>(url: string, data?: any, config?: AxiosRequestConfig): Promise<T> {
    return this.executeWithRetry(() =>
      this.client.post<T>(url, data, config).then(res => res.data)
    );
  }
