  success: boolean;
  token?: string;
  user?: User;
  error?: string;
}

const logger = new Logger('AuthService');
const userRepo = new UserRepository();
const JWT_SECRET = process.env.JWT_SECRET || 'default-secret';

export async function authenticate(credentials: Credentials): Promise<AuthResult> {
  try {
    const user = await userRepo.findByUsername(credentials.username);
    if (!user) {
      logger.warn('Authentication failed: user not found', { username: credentials.username });
      return { success: false, error: 'Invalid credentials' };
    }

    const validPassword = await compare(credentials.password, user.passwordHash);
    if (!validPassword) {
      logger.warn('Authentication failed: invalid password', { username: credentials.username });
      return { success: false, error: 'Invalid credentials' };
    }

    const token = sign({ userId: user.id, username: user.username }, JWT_SECRET, {
      expiresIn: '24h'
    });

    logger.info('User authenticated successfully', { userId: user.id });
    return { success: true, token, user };
  } catch (error) {
    logger.error('Authentication error', error);
    return { success: false, error: 'Authentication failed' };
  }
}

export async function validateToken(token: string): Promise<User | null> {
  try {
    const payload = verify(token, JWT_SECRET) as { userId: string };
    return await userRepo.findById(payload.userId);
  } catch {
    return null;
  }
